import './App.css';
import Web3 from "web3";
import Auction from './truffle_abis/Auction.json';
import { Component, useEffect , useState } from 'react';

class App extends Component {

  // Load web3 

  async UNSAFE_componentWillMount()
  {
    await this.LoadWeb3();
    await this.LoadBlockChain();
  }

  async LoadWeb3(){
    if(window.ethereum)
    {
      window.web3 = new Web3(window.ethereum);
      await window.ethereum.enable();
    }
    else if(window.web3)
    {
      window.web3 = new Web3 (window.web3.currentProvider)
    }
    else{
      window.alert('No Wallet Detected');
    }
  }
  async LoadBlockChain(){
    const web3 = window.web3
    const account = await web3.eth.getAccounts(); // getting account that is connected
    //this.setState({connected_account:account});

    //Ganache Network ID
    const networkID = await web3.eth.net.getId();

    //Loading Contract
    const ContractData = Auction.networks[networkID];

    if(ContractData){

      const cont = new web3.eth.Contract(Auction.abi,ContractData.address);
      //console.log("Conctract: " , cont);
      const addr = await cont.methods.GetOwner().call();
      this.setState({auction_contract:cont});
      console.log("state contract: " , this.state.auction_contract); 
    }
    else
    {
      console.log("Nod Data");
    }
  }

  // states
  constructor(props){
    super(props)
    this.state ={
      auction_contract: {},
      connected_account:'0x00'
    };
  }
  // React Part
  render(){
    return (
      <div>
        <p>Test</p>
      </div>
    );
  }

}


export default App;
